                          <Badge className={priorityColors[requirement.priority]}>
                            <PriorityIcon className="h-3 w-3 mr-1" />
                            {requirement.priority === 'HIGH' ? 'Alta' : requirement.priority === 'MEDIUM' ? 'Media' : 'Baja'}
