            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-green-600">
                {requirements.filter(r => r.priority === 'LOW').length}
              </div>
              <div className="text-sm text-gray-500">Prioridad Baja</div>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  )
}
