<content>import React, { useState, useEffect } from 'react'
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card'
import { Button } from '@/components/ui/button'
import { Input } from '@/components/ui/input'
import { Label } from '@/components/ui/label'
import { Textarea } from '@/components/ui/textarea'
import { Badge } from '@/components/ui/badge'
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select'
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog'
import { supabase } from '@/lib/supabase'
import { useAuth } from '@/contexts/AuthContext'
import { useToast } from '@/hooks/use-toast'
import {
  Plus,
  Edit2,
  Trash2,
  AlertTriangle,
  CheckCircle,
  Clock,
  Filter
} from 'lucide-react'

interface UserRequirement {
  id?: string
  requirement_text: string
  category: string
  priority: 'HIGH' | 'MEDIUM' | 'LOW'
  created_at?: string
}

const categories = [
  'Funcional',
  'No Funcional',
  'Seguridad',
  'Rendimiento',
  'Usabilidad',
  'Compatibilidad',
  'Mantenimiento',
  'Regulatory'
]

const priorityColors = {
  HIGH: 'bg-red-100 text-red-800',
  MEDIUM: 'bg-yellow-100 text-yellow-800',
  LOW: 'bg-green-100 text-green-800'
}

const priorityIcons = {
  HIGH: AlertTriangle,
  MEDIUM: Clock,
  LOW: CheckCircle
}

export default function UserRequirements() {
  const { user } = useAuth()
  const { toast } = useToast()
  const [requirements, setRequirements] = useState<UserRequirement[]>([])
  const [filteredRequirements, setFilteredRequirements] = useState<UserRequirement[]>([])
  const [loading, setLoading] = useState(true)
  const [isDialogOpen, setIsDialogOpen] = useState(false)
  const [editingRequirement, setEditingRequirement] = useState<UserRequirement | null>(null)
  const [filterCategory, setFilterCategory] = useState<string>('all')
  const [filterPriority, setFilterPriority] = useState<string>('all')
  const [newRequirement, setNewRequirement] = useState<UserRequirement>({
    requirement_text: '',
    category: '',
    priority: 'MEDIUM'
  })

  useEffect(() => {
    fetchRequirements()
  }, [user])

  useEffect(() => {
    applyFilters()
  }, [requirements, filterCategory, filterPriority])

  const fetchRequirements = async () => {
    if (!user) return

    try {
      const { data, error } = await supabase
        .from('app_a06fa33f4c_user_requirements')
        .select('*')
        .eq('user_id', user.id)
        .order('created_at', { ascending: false })

      if (error) throw error

      setRequirements(data || [])
    } catch (error) {
      console.error('Error fetching requirements:', error)
      toast({
        title: "Error",
        description: "No se pudieron cargar los requerimientos",
        variant: "destructive"
      })
    } finally {
      setLoading(false)
    }
  }

  const applyFilters = () => {
    let filtered = requirements

    if (filterCategory !== 'all') {
      filtered = filtered.filter(req => req.category === filterCategory)
    }

    if (filterPriority !== 'all') {
      filtered = filtered.filter(req => req.priority === filterPriority)
    }

    setFilteredRequirements(filtered)
  }

  const saveRequirement = async () => {
    if (!user || !newRequirement.requirement_text || !newRequirement.category) {
      toast({
        title: "Error",
        description: "Por favor complete todos los campos requeridos",
        variant: "destructive"
      })
      return
    }

    try {
      if (editingRequirement) {
        // Update existing
        const { error } = await supabase
          .from('app_a06fa33f4c_user_requirements')
          .update({
            requirement_text: newRequirement.requirement_text,
            category: newRequirement.category,
            priority: newRequirement.priority
          })
          .eq('id', editingRequirement.id)

        if (error) throw error
      } else {
        // Create new
        const { error } = await supabase
          .from('app_a06fa33f4c_user_requirements')
          .insert({
            requirement_text: newRequirement.requirement_text,
            category: newRequirement.category,
            priority: newRequirement.priority,
            user_id: user.id
          })

        if (error) throw error
      }

      toast({
        title: "Éxito",
        description: `Requerimiento ${editingRequirement ? 'actualizado' : 'creado'} correctamente`
      })

      setIsDialogOpen(false)
      setEditingRequirement(null)
      setNewRequirement({
        requirement_text: '',
        category: '',
        priority: 'MEDIUM'
      })
      fetchRequirements()
    } catch (error) {
      console.error('Error saving requirement:', error)
      toast({
        title: "Error",
        description: "No se pudo guardar el requerimiento",
        variant: "destructive"
      })
    }
  }

  const editRequirement = (requirement: UserRequirement) => {
    setEditingRequirement(requirement)
    setNewRequirement({
      requirement_text: requirement.requirement_text,
      category: requirement.category,
      priority: requirement.priority
    })
    setIsDialogOpen(true)
  }

  const deleteRequirement = async (id: string) => {
    try {
      const { error } = await supabase
        .from('app_a06fa33f4c_user_requirements')
        .delete()
        .eq('id', id)

      if (error) throw error

      toast({
        title: "Éxito",
        description: "Requerimiento eliminado correctamente"
      })

      fetchRequirements()
    } catch (error) {
      console.error('Error deleting requirement:', error)
      toast({
        title: "Error",
        description: "No se pudo eliminar el requerimiento",
        variant: "destructive"
      })
    }
  }

  const resetDialog = () => {
    setEditingRequirement(null)
    setNewRequirement({
      requirement_text: '',
      category: '',
      priority: 'MEDIUM'
    })
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <h1 className="text-3xl font-bold text-gray-900">Requerimientos de Usuario</h1>
        <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
          <DialogTrigger asChild>
            <Button onClick={resetDialog} className="flex items-center space-x-2">
              <Plus className="h-4 w-4" />
              <span>Nuevo Requerimiento</span>
            </Button>
          </DialogTrigger>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>
                {editingRequirement ? 'Editar' : 'Crear'} Requerimiento
              </DialogTitle>
              <DialogDescription>
                {editingRequirement
                  ? 'Modifica los detalles del requerimiento'
                  : 'Crea un nuevo requerimiento de usuario para el sistema'}
              </DialogDescription>
            </DialogHeader>
            <div className="space-y-4">
              <div>
                <Label htmlFor="requirement_text">Descripción del Requerimiento</Label>
                <Textarea
                  id="requirement_text"
                  value={newRequirement.requirement_text}
                  onChange={(e) => setNewRequirement(prev => ({
                    ...prev,
                    requirement_text: e.target.value
                  }))}
                  placeholder="Describe el requerimiento funcional o no funcional..."
                  rows={4}
                />
              </div>
              <div>
                <Label htmlFor="category">Categoría</Label>
                <Select
                  value={newRequirement.category}
                  onValueChange={(value) => setNewRequirement(prev => ({
                    ...prev,
                    category: value
                  }))}
                >
                  <SelectTrigger>
                    <SelectValue placeholder="Selecciona una categoría" />
                  </SelectTrigger>
                  <SelectContent>
                    {categories.map(category => (
                      <SelectItem key={category} value={category}>
                        {category}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
              <div>
                <Label htmlFor="priority">Prioridad</Label>
                <Select
                  value={newRequirement.priority}
                  onValueChange={(value: 'HIGH' | 'MEDIUM' | 'LOW') => setNewRequirement(prev => ({
                    ...prev,
                    priority: value
                  }))}
                >
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="HIGH">Alta</SelectItem>
                    <SelectItem value="MEDIUM">Media</SelectItem>
                    <SelectItem value="LOW">Baja</SelectItem>
                  </SelectContent>
                </Select>
              </div>
              <div className="flex space-x-2 pt-4">
                <Button onClick={saveRequirement} className="flex-1">
                  {editingRequirement ? 'Actualizar' : 'Crear'} Requerimiento
                </Button>
                <Button
                  variant="outline"
                  onClick={() => setIsDialogOpen(false)}
                >
                  Cancelar
                </Button>
              </div>
            </div>
          </DialogContent>
        </Dialog>
      </div>

      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <div>
              <CardTitle>Lista de Requerimientos</CardTitle>
              <CardDescription>
                Gestiona los requerimientos funcionales y no funcionales del sistema
              </CardDescription>
            </div>
            <div className="flex items-center space-x-2">
              <Filter className="h-4 w-4 text-gray-500" />
              <Select value={filterCategory} onValueChange={setFilterCategory}>
                <SelectTrigger className="w-40">
                  <SelectValue placeholder="Categoría" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">Todas las categorías</SelectItem>
                  {categories.map(category => (
                    <SelectItem key={category} value={category}>
                      {category}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
              <Select value={filterPriority} onValueChange={setFilterPriority}>
                <SelectTrigger className="w-32">
                  <SelectValue placeholder="Prioridad" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">Todas</SelectItem>
                  <SelectItem value="HIGH">Alta</SelectItem>
                  <SelectItem value="MEDIUM">Media</SelectItem>
                  <SelectItem value="LOW">Baja</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </div>
        </CardHeader>
        <CardContent>
          {loading ? (
            <div className="space-y-4">
              {[...Array(5)].map((_, i) => (
                <div key={i} className="p-4 border rounded-lg animate-pulse">
                  <div className="h-4 bg-gray-200 rounded w-3/4 mb-2"></div>
                  <div className="h-3 bg-gray-200 rounded w-1/2"></div>
                </div>
              ))}
            </div>
          ) : filteredRequirements.length === 0 ? (
            <div className="text-center py-8">
              <ClipboardList className="h-16 w-16 text-gray-300 mx-auto mb-4" />
              <p className="text-gray-500">
                {requirements.length === 0
                  ? 'No hay requerimientos creados. Crea el primero.'
                  : 'No se encontraron requerimientos con los filtros aplicados.'}
              </p>
            </div>
          ) : (
            <div className="space-y-4">
              {filteredRequirements.map((requirement) => {
                const PriorityIcon = priorityIcons[requirement.priority]
                return (
                  <div key={requirement.id} className="p-4 border border-gray-200 rounded-lg hover:shadow-md transition-shadow">
                    <div className="flex items-start justify-between">
                      <div className="flex-1">
                        <div className="flex items-center space-x-2 mb-2">
                          <Badge variant="secondary">{requirement.category}</Badge>
                          <Badge className={priorityColors[requirement.priority]}>
                            <PriorityIcon className="h-3 w-3 mr-1" />
                            {requirement.priority === 'HIGH' ? 'Alta' : requirement.priority === 'MEDIUM' ? 'Media' : 'Baja'}
                          </Badge>
                        </div>
                        <p className="text-gray-900 mb-2">{requirement.requirement_text}</p>
                        <p className="text-xs text-gray-500">
                          Creado el {new Date(requirement.created_at!).toLocaleDateString('es-ES')}
                        </p>
                      </div>
                      <div className="flex items-center space-x-2 ml-4">
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => editRequirement(requirement)}
                        >
                          <Edit2 className="h-4 w-4" />
                        </Button>
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => deleteRequirement(requirement.id!)}
                          className="text-red-600 hover:text-red-700"
                        >
