const statusColors = {
  DRAFT: 'bg-gray-100 text-gray-800',
  FINAL: 'bg-green-100 text-green-800'
}

const statusIcons = {
  DRAFT: Clock,
  FINAL: CheckCircle
}

const defaultSections: ReportSection[] = [
