          reviewed_by: '',
          approved_by: '',
          version: '1.0'
        }
      },
      status: report.status
    })
    setIsDialogOpen(true)
  }

  const deleteReport = async (id: string) => {
    try {
      const { error } = await supabase
        .from('app_a06fa33f4c_validation_reports')
