          reviewed_by: '',
          approved_by: '',
          version: '1.0'
        }
      },
      status: 'DRAFT'
    })
    setActiveSection('1')
  }

  const updateSection = (sectionId: string, content: string) => {
    setNewReport(prev => ({
      ...prev,
      content: {
        ...prev.content,
