          section.id === sectionId ? { ...section, content } : section
        )
      }
    }))
  }

  const updateMetadata = (field: string, value: string) => {
    setNewReport(prev => ({
      ...prev,
      content: {
        ...prev.content,
        metadata: {
          ...prev.content.metadata,
          [field]: value
        }
      }
    }))
  }

  return (
