  background-color: #f8fafc;
}

.main-layout {
  display: flex;
  min-height: 100vh;
}

.content-area {
  flex: 1;
  display: flex;
  flex-direction: column;
}

.page-content {
  flex: 1;
  padding: 2rem;
  overflow-y: auto;
}

/* Canvas styles for Process Mapping */
.process-canvas {
  border: 1px solid #e2e8f0;
  border-radius: 0.5rem;
  background: white;
  cursor: crosshair;
}

.process-canvas.dragging {
  cursor: move;
}

/* Custom scrollbar styles */
.custom-scrollbar::-webkit-scrollbar {
  width: 6px;
}

.custom-scrollbar::-webkit-scrollbar-track {
  background: #f1f5f9;
  border-radius: 3px;
}

.custom-scrollbar::-webkit-scrollbar-thumb {
  background: #cbd5e1;
  border-radius: 3px;
}

.custom-scrollbar::-webkit-scrollbar-thumb:hover {
  background: #94a3b8;
}

/* Animation classes */
.fade-in {
  animation: fadeIn 0.3s ease-in-out;
}

@keyframes fadeIn {
  from {
    opacity: 0;
    transform: translateY(10px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

/* Loading animation */
.loading-spinner {
  display: inline-block;
  width: 20px;
  height: 20px;
  border: 3px solid #f3f3f3;
  border-top: 3px solid #3498db;
  border-radius: 50%;
  animation: spin 1s linear infinite;
}

@keyframes spin {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

/* Risk level indicators */
.risk-high {
  background-color: #fee2e2;
  color: #991b1b;
  border: 1px solid #fca5a5;
}

.risk-medium {
  background-color: #fef3c7;
  color: #92400e;
  border: 1px solid #fcd34d;
}

.risk-low {
  background-color: #d1fae5;
  color: #065f46;
  border: 1px solid #6ee7b7;
}

/* Status indicators */
.status-draft {
  background-color: #f3f4f6;
  color: #374151;
}

.status-approved {
  background-color: #d1fae5;
  color: #065f46;
}

.status-executed {
  background-color: #dbeafe;
  color: #1e40af;
}

.status-passed {
  background-color: #d1fae5;
  color: #065f46;
}

.status-failed {
  background-color: #fee2e2;
  color: #991b1b;
}

.status-pending {
  background-color: #fef3c7;
  color: #92400e;
}

/* Responsive design */
@media (max-width: 768px) {
  .page-content {
    padding: 1rem;
  }

  .main-layout {
    flex-direction: column;
  }
}

/* Print styles */
@media print {
  .no-print {
    display: none !important;
  }

  .page-content {
    padding: 0;
  }
}
</content>
</Editor.write>

